import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';
import sinonChai from 'sinon-chai';

import { generateGUID } from '../src/utils/utils';
import AddressAllocation from '../src/allocation/AddressAllocation';

chai.config.truncateThreshold = 0;

let expect = chai.expect;

chai.use(chaiAsPromised);
chai.use(sinonChai);

describe('AddressAllocation', function() {

  let aa;
  let registry;
  let bus;
  let guid = generateGUID();
  let domain = 'sp.domain';

  before(() => {

    registry = {
      registerHyperty: () => {},
      checkRegisteredURLs: () => {}
    };

    bus = {
      postMessage: (msg, replyCallback) => {

        if (!msg.body.scheme) { msg.body.scheme = 'hyperty'; }

        replyCallback({
          id: 1, type: 'response', from: 'domain://msg-node.sp.domain/address-allocation', to: 'local://fake.url',
          body: {code: 200, value: {allocated: msg.body.scheme + '://' + domain + '/' + guid}}
        });

      }
    };

    sinon.stub(registry, 'checkRegisteredURLs', (info) => {

      return new Promise((resolve) => {

        console.log('CHECK REGISTER: ', info);

        if (info.reporter.length === 0) {
          resolve('hyperty://' + domain + '/' + guid);
        } else {
          resolve('comm://' + domain + '/' + guid);
        }

      });

    });

    let runtimeURL = 'hyperty-runtime://ua.pt/123';
    new AddressAllocation(runtimeURL, bus, registry);
    aa = AddressAllocation.instance;
  });

  it('should create a new hyperty address', function(done) {

    let number = 2;
    let scheme = 'hyperty';
    let info = {
      name: 'test',
      schema: 'hyperty-catalogue://' + domain + '/.well-known/dataschema/hello',
      reporter: [],
      resources: []
    };
    expect(aa.create(domain, number, info, scheme))
    .eventually.to.eql({newAddress: true, address: 'hyperty://' + domain + '/' + guid})
    .notify(done);
  });

  it('should create a new data Object address', function(done) {

    let number = 2;
    let scheme = 'comm';
    let info = {
      name: 'dataObjectName',
      schema: 'hyperty-catalogue://' + domain + '/.well-known/dataschema/communication',
      reporter: ['comm://' + domain + '/' + guid],
      resources: ['chat']
    };
    expect(aa.create(domain, number, info, scheme))
    .eventually.to.eql({newAddress: true, address: 'comm://' + domain + '/' + guid})
    .notify(done);
  });


  it('should reuse an hyperty url address based on reuse option', function(done) {

    let number = 1;
    let scheme = 'hyperty';
    let info = {
      name: 'test',
      schema: 'hyperty-catalogue://' + domain + '/.well-known/dataschema/hello',
      reporter: [],
      resources: []
    };

    expect(aa.create(domain, number, info, scheme, true))
    .eventually.to.eql({newAddress: false, address: 'hyperty://' + domain + '/' + guid})
    .notify(done);

  });

  it('should reuse an hyperty url address based on an given url', function(done) {

    let number = 1;
    let scheme = 'hyperty';
    let info = {
      name: 'test',
      schema: 'hyperty-catalogue://' + domain + '/.well-known/dataschema/hello',
      reporter: [],
      resources: []
    };

    expect(aa.create(domain, number, info, scheme, 'hyperty://' + domain + '/' + guid))
    .eventually.to.eql({newAddress: false, address: 'hyperty://' + domain + '/' + guid})
    .notify(done);

  });

});

import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';
import sinonChai from 'sinon-chai';

let expect = chai.expect;
chai.use(chaiAsPromised);
chai.use(sinonChai);

// dependencies
import MessageBus from '../src/bus/MessageBus';
import Descriptors from '../src/runtime/Descriptors';
import { runtimeFactory } from './resources/runtimeFactory';
import GraphConnector from '../src/graphconnector/GraphConnector';
import CoreDiscovery from '../src/discovery/CoreDiscovery';
import Registry from '../src/registry/Registry';
import AddressAllocation from '../src/allocation/AddressAllocation';

let msgbus;
let runtimeURL = 'hyperty-runtime://ua.pt/123';
let domain = 'ua.pt';
let appSandbox = runtimeFactory.createAppSandbox();
let storageManager = runtimeFactory.storageManager();
let runtimeCatalogue = runtimeFactory.createRuntimeCatalogue();
let runtimeCapabilities = runtimeFactory.runtimeCapabilities(storageManager);
let coreDiscovery;

let hyperty = {'hyperty://ist.pt/1':
                {descriptor: 'hyperty-catalogue://ist.pt/.well-known/hyperty/HelloHyperty',
                 lastModified: '"2016-03-03T13:32:06Z"',
                 dataSchemes: ['comm'],
                 resources:   ['chat']}
};

let dataObject = {'comm://ist.pt/1':
                {schema: 'hyperty-catalogue://catalogue.hybroker.rethink.ptinovacao.pt/.well-known/dataschema/Communication',
                 url: 'comm://ist.pt/1',
                 name: 'mychat',
                 lastModified: '"2016-03-03T13:32:06Z"',
                 dataSchemes: ['comm'],
                 resources:   ['chat']}
};

describe('CoreDiscovery', function() {

  // this will be executed before all the tests, and you can garantee this will be setted
  before(() => {

      let identityModule = {
          getIdentities: () => {
              let identities = [];
              let identityBundle = {identity: 'user://gmail.com/openidtest10', token: 'idToken'};
              identities.push(identityBundle);
              return identities;
          }
      };

      // instanciate the registry;
      let registry = new Registry(runtimeURL, appSandbox, identityModule, runtimeCatalogue, runtimeCapabilities, storageManager);
      msgbus = new MessageBus(registry);
      new AddressAllocation(runtimeURL, msgbus, registry);
      registry.messageBus = msgbus;
      // Prepare the on instance to handle with the fallbacks and runtimeCatalogue;
      let descriptorInstance = new Descriptors(runtimeURL, runtimeCatalogue, {});
      let graphConnector = new GraphConnector(runtimeURL, msgbus, storageManager);
      coreDiscovery = new CoreDiscovery(runtimeURL, msgbus, graphConnector, runtimeFactory);

      coreDiscovery.messageBus.addListener('domain://registry.ua.pt/', (msg) => {
        console.info(msg);
        let message_hyperties = {
            id: msg.id, type: 'response', from: 'domain://registry.ua.pt/', to: msg.from,
            body: {
                code: 200,
                value: [hyperty]
            }
        };

        let message_hyperty = {
            id: msg.id, type: 'response', from: 'domain://registry.ua.pt/', to: msg.from,
            body: {
                code: 200,
                value: hyperty
            }
        };

        let message_dataObjects = {
            id: msg.id, type: 'response', from: 'domain://registry.ua.pt/', to: msg.from,
            body: {
                code: 200,
                value: [dataObject]
            }
        };

        let message_dataObject = {
            id: msg.id, type: 'response', from: 'domain://registry.ua.pt/', to: msg.from,
            body: {
                code: 200,
                value: dataObject
            }
        };

        if(msg.body.resource === '/hyperty/idp-identifier/openidtest20@gmail.com')
          coreDiscovery.messageBus.postMessage(message_hyperties); //discoverHyperties
        else if(msg.body.resource === '/comm')
          coreDiscovery.messageBus.postMessage(message_dataObjects); //discoverDataObjects && //discoverDataObjectsPerReporter
        else if (msg.body.resource === 'hyperty://ist.pt/1')
          coreDiscovery.messageBus.postMessage(message_hyperty); //discoverHypertyPerURL
        else if (msg.body.resource === 'comm://ist.pt/1')
          coreDiscovery.messageBus.postMessage(message_dataObject); //discoverDataObjectPerURL
        else if (msg.body.resource === 'myChat')
          coreDiscovery.messageBus.postMessage(message_dataObjects); //discoverDataObjectsPerName

      });
  });

  describe('construction', function() {
      it('should create a new CoreDiscovery without error', function() {
          expect(coreDiscovery).to.be.instanceof(CoreDiscovery);
      });
  });

  describe('discoverHyperties()', function() {
    it('should conclude the advanced search without error', function(done) {

      expect(coreDiscovery.discoverHyperties('openidtest20@gmail.com', ['comm'], ['chat'], domain).then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql([hyperty]).and.notify(done);
    });
  });

  describe('discoverDataObjects()', function() {
    it('should conclude the advanced search without error', function(done) {

      expect(coreDiscovery.discoverDataObjects('openidtest20@gmail.com', ['comm'], ['chat'], domain).then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql([dataObject]).and.notify(done);
    });
  });

  describe('discoverHypertyPerURL()', function() {
    it('should conclude the advanced search without error', function(done) {

      expect(coreDiscovery.discoverHypertyPerURL('hyperty://ist.pt/1').then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql(hyperty).and.notify(done);
    });
  });

  describe('discoverDataObjectPerURL()', function() {
    it('should conclude the advanced search without error', function(done) {

      expect(coreDiscovery.discoverDataObjectPerURL('comm://ist.pt/1').then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql(dataObject).and.notify(done);
    });
  });

  describe('discoverDataObjectsPerName()', function() {
    it('should conclude the advanced search without error', function(done) {

      expect(coreDiscovery.discoverDataObjectsPerName('myChat', ['comm'], ['chat']).then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql([dataObject]).and.notify(done);
    });
  });

  describe('discoverDataObjectsPerReporter()', function() {
    it('should conclude the advanced search without error', function(done) {

      expect(coreDiscovery.discoverDataObjectsPerReporter('hyperty://ist.pt/1', ['comm'], ['chat']).then(function(response) {
        return response;
      })).to.be.fulfilled.and.eventually.eql([dataObject]).and.notify(done);
    });
  });

});

import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';

import DataObjectsStorage from '../src/store-objects/DataObjectsStorage';
import { runtimeFactory } from './resources/runtimeFactory';

chai.config.truncateThreshold = 0;

let expect = chai.expect;
chai.use(chaiAsPromised);

describe('dataObjectsStorage', function() {

  let storageManager;
  let dataObjectsStorage;
  let runtimeURL = 'hyperty-runtime://fake-runtime';
  let syncherManagerURL = runtimeURL + '/sm';

  let owner = 'hyperty://<domain>/id-owner';
  let url = 'resource://obj1';
  let userURL = 'user://<domain>/<my-name>';
  let schema = 'schema://fake-schema-url';
  let schemaList = ['schema://fake-schema-url/Communication', 'schema://fake-schema-url/Context', 'schema://fake-schema-url/Connection'];

  before(() => {

    storageManager = runtimeFactory.storageManager();
    dataObjectsStorage = new DataObjectsStorage(storageManager, {});

  });

  it('should set dataObject information to be saved on storage', (done) => {

    let metadata = {};

    metadata.status = 'live';
    metadata.isReporter = true;
    let data = {
      x: 1,
      y: 2
    };
    metadata.schema = schema;
    metadata.url = url;
    metadata.name = 'WebRTC';
    metadata.subscriberUsers = [];
    metadata.subscriptions = [];
    metadata.version = 0;
    metadata.reporter = 'hyperty://<domain>/id-3';
    let subscriberHyperty = 'hyperty://<domain>/id-2';
    let childrenObjects = {};
    childrenObjects[metadata.reporter + '#1'] = { message: 'message 1' };
    childrenObjects[subscriberHyperty + '#1'] = { message: 'message 2' };
    childrenObjects[metadata.reporter + '#2'] = { message: 'message 3' };

    expect(dataObjectsStorage.set(metadata))
    .to.have.keys('url', 'isReporter', 'subscriberUsers', 'subscriptions', 'version', 'schema', 'status', 'reporter', 'name', 'childrenObjects', 'data');

    expect(dataObjectsStorage.saveData(true, url, null, data)).to.be.deep.equal({
      url: metadata.url,
      isReporter: metadata.isReporter,
      subscriptions: [],
      subscriberUsers: [],
      data: data,
      version: 0,
      schema: metadata.schema,
      status: metadata.status,
      reporter: metadata.reporter,
      childrenObjects: {},
      name: metadata.name
    });

    expect(dataObjectsStorage.saveChildrens(true, metadata.url, null, childrenObjects)).to.deep.equal({
      url: metadata.url,
      isReporter: metadata.isReporter,
      subscriptions: [],
      subscriberUsers: [],
      schema: metadata.schema,
      status: metadata.status,
      reporter: metadata.reporter,
      data: data,
      childrenObjects: childrenObjects,
      version: 0,
      name: metadata.name
    });

    expect(dataObjectsStorage.update(metadata.isReporter, metadata.url, 'subscriptions', subscriberHyperty)).to.be.deep.equal({
      url: metadata.url,
      isReporter: metadata.isReporter,
      subscriptions: [subscriberHyperty],
      subscriberUsers: [],
      schema: schema,
      status: metadata.status,
      reporter: metadata.reporter,
      data: data,
      childrenObjects: childrenObjects,
      version: 0,
      name: metadata.name
    });

    done();

  });

  it('should set a bundle of information to be saved on storage like reporter', (done) => {

    let num = 4;

    for (let i = 0; i < num; i++) {

      let metadata = {};

      metadata.status = 'live';
      metadata.version = 0;
      metadata.isReporter = true;
      metadata.url = '<scheme>://<domain>/id-' + i;
      let rand = Math.round(Math.random() * 2);
      metadata.schema = schemaList[rand];
      metadata.reporter = 'hyperty://<domain>/id-' + (num - i);
      let subscriberHyperty = 'hyperty://<domain>/id-' + ((num - i) * 2);

      // resource, isReporter, schema, status, data, subscription, children, childrenResources, subscriberUser
      expect(dataObjectsStorage.set(metadata)).to.be.deep.equal({
        url: metadata.url,
        isReporter: metadata.isReporter,
        subscriptions: [],
        subscriberUsers: [],
        childrenObjects: {},
        data: {},
        version: 0,
        schema: metadata.schema,
        status: metadata.status,
        reporter: metadata.reporter
      });

      expect(dataObjectsStorage.update(metadata.isReporter, metadata.url, 'store', true)).to.be.deep.equal({
        url: metadata.url,
        isReporter: metadata.isReporter,
        subscriptions: [],
        subscriberUsers: [],
        childrenObjects: {},
        data: {},
        version: 0,
        schema: metadata.schema,
        status: metadata.status,
        reporter: metadata.reporter,
        store: true
      });

      expect(dataObjectsStorage.saveData(metadata.isReporter, metadata.url, 'participants.1', {name: 'vitor', last: 'silva'})).to.be.deep.equal({
        url: metadata.url,
        isReporter: metadata.isReporter,
        subscriptions: [],
        subscriberUsers: [],
        childrenObjects: {},
        version: 0,
        schema: metadata.schema,
        status: metadata.status,
        reporter: metadata.reporter,
        store: true,
        data: {
          participants: {
            1: {
              name: 'vitor', last: 'silva'
            }
          }
        }
      });

      expect(dataObjectsStorage.update(metadata.isReporter, metadata.url, 'subscriptions', subscriberHyperty)).to.be.deep.equal({
        url: metadata.url,
        isReporter: metadata.isReporter,
        subscriptions: [subscriberHyperty],
        subscriberUsers: [],
        childrenObjects: {},
        version: 0,
        schema: metadata.schema,
        status: metadata.status,
        reporter: metadata.reporter,
        store: true,
        data: {
          participants: {
            1: {
              name: 'vitor', last: 'silva'
            }
          }
        }
      });

      if (i === num - 1) {
        console.log('DONE', i);
        done();
      }
    }

  });

  it('should set a bundle of information to be saved on storage like observers', (done) => {

    let num = 4;
    let letters = ['A', 'B', 'C', 'D'];

    for (let i = 0; i < num; i++) {

      let metadata = {};

      metadata.status = 'live';
      metadata.version = 0;
      metadata.isReporter = false;
      metadata.url = '<scheme>://<domain>/id-' + letters[i];
      let rand = Math.round(Math.random() * 2);
      metadata.schema = schemaList[rand];
      metadata.reporter = 'hyperty://<domain>/id-' + (num - i);
      let subscriberHyperty = 'hyperty://<domain>/id-' + letters[(num - i)];

      // resource, isReporter, schema, status, data, subscription, children, childrenResources, subscriberUser
      expect(dataObjectsStorage.set(metadata)).to.be.deep.equal({
        url: metadata.url,
        isReporter: metadata.isReporter,
        subscriptions: [],
        subscriberUsers: [],
        childrenObjects: {},
        data: {},
        version: 0,
        schema: metadata.schema,
        status: metadata.status,
        reporter: metadata.reporter
      });

      if (i === num - 1) {
        done();
      }
    }

  });

  it('should update a resource with new Hyperty Subscribers', (done) => {
    let url = '<scheme>://<domain>/id-2';
    let hypertySubscribers = ['hyperty://<domain>/id-3', 'hyperty://<domain>/id-2'];
    let isReporter = true;

    expect(dataObjectsStorage.update(isReporter, url, 'hypertySubscribers', hypertySubscribers[0]).hypertySubscribers).to.contains(hypertySubscribers[0], hypertySubscribers[1]);

    // .to.have.deep.property('subscriptions', ['hyperty://<domain>/id-4', 'hyperty://<domain>/id-3']);

    // expect(dataObjectsStorage._storeDataObject[type][resource].subscriptions).to.contains(subscriptions[0], subscriptions[1]);
    done();
  });

  it('should update a resource with new Subscriber Users', (done) => {
    let resource = '<scheme>://<domain>/id-2';
    let isReporter = true;

    expect(dataObjectsStorage.update(isReporter, resource, 'subscriberUsers', userURL).subscriberUsers).to.contains(userURL);
    done();
  });

  // it('should update the data resource', (done) => {
  //   let resource = '<scheme>://<domain>/id-2';
  //   let isReporter = true;
  //   dataObjectsStorage.updateData(resource, 'subscriberUsers', userURL, isReporter);
  //
  //   let type = isReporter ? 'reporters' : 'observers';
  //
  //   expect(dataObjectsStorage._storeDataObject[type][resource].subscriberUsers).to.contains(userURL);
  //   done();
  // });

  it('should get specific dataObject by hypertyURL', (done) => {

    let msg = {
      type: 'create',
      from: 'hyperty://<domain>/id-2',
      to: syncherManagerURL
    };

    expect(dataObjectsStorage.getResourcesByCriteria(msg, true))
    .to.be.fulfilled
    .and.eventually.to.include.keys('<scheme>://<domain>/id-2')
    .and.notify(done);

  });

  it('should get specific dataObject by identity', (done) => {

    let msg = {
      type: 'create',
      from: 'hyperty://<domain>/id-3',
      to: syncherManagerURL,
      body: {
        identity: userURL
      }

    };

    expect(dataObjectsStorage.getResourcesByCriteria(msg, true))
    .to.be.fulfilled
    .and.eventually.to.include.keys('<scheme>://<domain>/id-2')
    .and.notify(done);

  });

  it('should get specific dataObject by schema', (done) => {

    let msg = {
      type: 'create',
      from: 'hyperty://<domain>/id-3',
      to: syncherManagerURL,
      body: {
        schema: schema
      }

    };

    expect(dataObjectsStorage.getResourcesByCriteria(msg, true))
    .to.be.fulfilled
    .and.eventually.to.include.keys(url)
    .and.notify(done);

  });

  it('should get specific dataObject by some data', (done) => {

    let msg = {
      type: 'create',
      from: 'hyperty://<domain>/id-3',
      to: syncherManagerURL,
      body: {
        value: {
          name: 'WebRTC'
        }
      }

    };

    expect(dataObjectsStorage.getResourcesByCriteria(msg, true))
    .to.be.fulfilled
    .and.eventually.to.include.keys(url)
    .and.notify(done);

  });

  it('should delete a specific value from specific resource', (done) => {
    let resource = '<scheme>://<domain>/id-3';
    let subscription = 'hyperty://<domain>/id-1';
    dataObjectsStorage.delete(resource, 'subscriptions', subscription);

    done();
  });

  it('should not delete a resource', (done) => {
    let resource = '<scheme>://<domain>/id-8';
    expect(dataObjectsStorage.deleteResource(resource)).to.be.fulfilled
    .and.eventually.to.be.eq('The ' + resource + ' dosen\t exists, nothing was deleted').and.notify(done);
  });

  it('should delete a resource', (done) => {
    let resource = '<scheme>://<domain>/id-1';
    expect(dataObjectsStorage.deleteResource(resource))
    .to.be.fulfilled
    .and.eventually.to.be.undefined.and.notify(done);
  });

});

import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';
import sinonChai from 'sinon-chai';

let expect = chai.expect;

chai.use(chaiAsPromised);
chai.use(sinonChai);

import { descriptors } from './resources/descriptors.js';

import { buildURL } from '../src/utils/utils';
import { runtimeConfiguration } from './resources/runtimeConfiguration';
import { runtimeFactory } from './resources/runtimeFactory';
import Descriptors from '../src/runtime/Descriptors';

describe('Should get configuration and parse to Descriptors', () => {

  // Testing Registry
  let domain = 'sp.domain';
  let runtimeURL = 'hyperty-runtime://' + domain + '/123';
  let descriptorsInstance;

  runtimeConfiguration.domain = domain;

  before(()=> {
    let catalogue = runtimeFactory.createRuntimeCatalogue();
    descriptorsInstance = new Descriptors(runtimeURL, catalogue, runtimeConfiguration);

    sinon.stub(descriptorsInstance.catalogue, 'getStubDescriptor', (url) => {
      return new Promise((resolve) => {
        resolve(descriptors.ProtoStubs.default);
      });
    });

    sinon.stub(descriptorsInstance.catalogue, 'getIdpProxyDescriptor', (url) => {
      return new Promise((resolve, reject) => {
        if (url.includes('catalogue.google.com')) {
          reject();
        } else {
          resolve(descriptors.IdpProxies['google.com']);
        }
      });
    });

    sinon.stub(descriptorsInstance.catalogue, 'getHypertyDescriptor', (url) => {
      return new Promise((resolve) => {
        resolve(descriptors.Hyperties.HelloHyperty);
      });
    });

  });

  after(() => {
    descriptorsInstance.catalogue.getIdpProxyDescriptor.restore();
    descriptorsInstance.catalogue.getHypertyDescriptor.restore();
    descriptorsInstance.catalogue.getStubDescriptor.restore();
  });

  it('constructor should receive 3 arguments', () => {

    expect(descriptorsInstance)
    .to.have.property('runtimeURL')
    .that.is.an('string')
    .to.not.be.empty;

    expect(descriptorsInstance)
    .to.have.property('catalogue');

    expect(descriptorsInstance)
    .to.have.property('runtimeConfiguration')
    .that.is.a('object')
    .and.to.contain.all.keys(runtimeConfiguration);

  });

  it('should get hyperty', (done) => {

    let hypertyDescriptorURL = 'hyperty-catalogue://catalogue.sp.domain/.well-known/hyperty/Connector';

    expect(descriptorsInstance.getHypertyDescriptor(hypertyDescriptorURL))
    .to.be.fulfilled
    .and.notify(done);

  });

  it('should get protocolstub', (done) => {

    let stubDescriptorURL = 'sp.domain';

    expect(descriptorsInstance.getStubDescriptor(stubDescriptorURL))
    .to.be.fulfilled
    .and.notify(done);

  });

  it('should get protocolstub', (done) => {

    let idpProxyURL = 'google.com';

    expect(descriptorsInstance.getIdpProxyDescriptor(idpProxyURL))
    .to.be.fulfilled
    .and.notify(done);

  });

});

import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';

chai.config.truncateThreshold = 0;

let expect = chai.expect;
chai.use(chaiAsPromised);

// dependencies
import BloomFilter from '../src/graphconnector/BloomFilter';
import GraphConnector from '../src/graphconnector/GraphConnector';
import GraphConnectorContactData from '../src/graphconnector/GraphConnectorContactData';
import bip39 from 'bip39';
import jsrsasign from 'jsrsasign';
import Registry from '../src/registry/Registry';
import MessageBus from '../src/bus/MessageBus';
import { runtimeFactory } from './resources/runtimeFactory';
import AddressAllocation from '../src/allocation/AddressAllocation';

let registry;
let msgbus;
let runtimeURL = 'hyperty-runtime://ua.pt/123';
let appSandbox = runtimeFactory.createAppSandbox();
let storageManager = runtimeFactory.storageManager();
let runtimeCatalogue = runtimeFactory.createRuntimeCatalogue();
let runtimeCapabilities = runtimeFactory.runtimeCapabilities(storageManager);
let graphConnector;

describe('Graph Connector', function() {

    // this will be executed before all the tests, and you can garantee this will be setted
    before(function () {

        let identityModule = {
            getIdentities: () => {
                let identities = [];
                let identityBundle = {identity: 'user://gmail.com/openidtest10', token: 'idToken'};
                identities.push(identityBundle);
                return identities;
            }
        };

