import AddressAllocation from '../src/allocation/AddressAllocation';

// Testing Registry
let runtimeURL = 'hyperty-runtime://ua.pt/123';

let sandboxDummyCapabilities = {browser: true};
let storageManager = runtimeFactory.storageManager();
