import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';
import sinonChai from 'sinon-chai';

chai.config.truncateThreshold = 0;

let expect = chai.expect;

chai.use(chaiAsPromised);
chai.use(sinonChai);

import { descriptors } from './resources/descriptors.js';

// Testing Module
import RuntimeUA from  '../src/runtime/RuntimeUA';

// Main dependecies
import Registry from '../src/registry/Registry';
import IdentityModule from '../src/identity/IdentityModule';
import PEP from '../src/policy/PEP';
import MessageBus from '../src/bus/MessageBus';

import { divideURL } from '../src/utils/utils';

import { runtimeFactory } from './resources/runtimeFactory';

/// import { runtimeConfiguration } from './resources/runtimeConfiguration';

// Testing runtimeUA;
let domain = 'localhost';
describe('RuntimeUA', function() {

  let runtime = new RuntimeUA(descriptors.Runtimes.Runtime, runtimeFactory, domain);
  let getDescriptor;

  before(function() {

    getDescriptor = (url) => {

      return new Promise(function(resolve, reject) {

        let dividedURL = divideURL(url);
        let identity = dividedURL.identity;

        if (!identity) {
          identity = 'default';
        } else {
          identity = identity.substring(identity.lastIndexOf('/') + 1);
        }

        let result;

        if (url.includes('hyperty')) {
          try {
            result = descriptors.Hyperties[identity];
          } catch (e) {
            reject(e);
          }

        } else if (url.includes('protocolstub') || url === dividedURL.domain) {
          try {
            result = descriptors.ProtoStubs[identity];
          } catch (e) {
            reject(e);
          }
        } else if (url.includes('idp-proxy')) {
          try {
            result = descriptors.IdpProxies[identity];
          } catch (e) {
            reject(e);
          }
        } else if (url.includes('dataschema')) {
          try {
            result = descriptors.DataSchemas[identity];
          } catch (e) {
            reject(e);
          }

        }

        resolve(result);

      });
    };
  });

  after(function() {
    runtime.descriptorInstance.getHypertyDescriptor.restore();
    runtime.descriptorInstance.getStubDescriptor.restore();
    runtime.descriptorInstance.getIdpProxyDescriptor.restore();
  });

  describe('constructor()', function() {

    it('expects the runtime was ready', (done) => {

      expect(runtime.init().then((result) => {

        sinon.stub(runtime.messageBus, 'postMessage', function(msg, replyCallback) {
          replyCallback({
            id: 1, type: 'response', from: 'domain://msg-node.sp.domain/address-allocation', to: 'local://fake.url',
            body: {code: 200, value: {allocated: msg.body.scheme + '://sp.domain/9c8c1949-e08e-4554-b201-bab201bdb21d'}}
          });
        });

        sinon.stub(runtime.descriptorInstance, 'getHypertyDescriptor', (hypertyURL) => {
          return getDescriptor(hypertyURL);
        });

        sinon.stub(runtime.descriptorInstance, 'getStubDescriptor', (stubURL) => {
          return getDescriptor(stubURL);
        });

        sinon.stub(runtime.descriptorInstance, 'getIdpProxyDescriptor', (idpProxyURL) => {
          return getDescriptor(idpProxyURL);
        });

        sinon.stub(runtime.runtimeCatalogue, 'getDataSchemaDescriptor', (dataSchemaURL) => {
          return getDescriptor(dataSchemaURL);
        });

        sinon.stub(runtime.registry, 'registerHyperty', (sandbox, descriptorURL, descriptor, addressURL) => {
          return new Promise(function(resolve) {
            console.log('AQIO:', addressURL);
            if (addressURL.newAddress) {
              resolve('hyperty://sp.domain/9c8c1949-e08e-4554-b201-bab201bdb21d');
            } else {
              resolve(addressURL.address);
            }
          });

        });

        sinon.stub(runtime.registry, 'checkRegisteredURLs', (info, reuseURL) => {

          return new Promise((resolve) => {
            console.log('checkRegisteredURLs:', typeof(reuseURL), reuseURL);
            if (typeof(reuseURL) === 'boolean') {
              resolve('hyperty://sp.domain/9c8c1949-e08e-4554-b201-bab201bdb21d');
            } else if (typeof(reuseURL) === 'string') {
              console.log('checkRegisteredURLs is string:', reuseURL);
              resolve(reuseURL);
            } else {
              resolve('hyperty://sp.domain/9c8c1949-e08e-4554-b201-bab201bdb21d');
            }

          });

        });

        return result;
      }))
      .to.be.fulfilled
      .and.to.eventually.be.true
      .and.notify(done);

    });

    it('depends of the Registry', function() {
      expect(runtime.registry).to.be.instanceof(Registry);
    });

    it('depends of the Identity Module', function() {
      expect(runtime.identityModule).to.be.instanceof(IdentityModule);
    });

    it('depends of the Policy Engine', function() {
      expect(runtime.policyEngine).to.be.instanceof(PEP);
    });

    it('depends of the MessageBus', function() {
      expect(runtime.messageBus).to.be.instanceof(MessageBus);
    });

    it('should throw when given no arguments', function() {
      expect(runtime).to.have.property('runtimeFactory');
      expect(runtime.runtimeFactory).to.have.property('atob');
      expect(runtime.runtimeFactory).to.have.property('persistenceManager');
      expect(runtime.runtimeFactory).to.have.property('runtimeCapabilities');
      expect(runtime.runtimeFactory).to.have.property('storageManager');
      expect(runtime.runtimeFactory).to.have.property('createSandbox');
      expect(runtime.runtimeFactory).to.have.property('createAppSandbox');
      expect(runtime.runtimeFactory).to.have.property('createHttpRequest');
      expect(runtime.runtimeFactory).to.have.property('createRuntimeCatalogue');
    });

  });

  describe('loadHyperty(hypertyDescriptorURL)', function() {

    it('should throw when given no arguments', function(done) {
      let hypertyDescriptorURL = 'hyperty-catalogue://catalogue.sp.domain/.well-known/hyperty/HelloHyperty';
      let loadHyperty = runtime.loadHyperty(hypertyDescriptorURL);

      expect(loadHyperty)
      .to.be.fulfilled
      .and.notify(done);
    });

    it('should be a Promise', function(done) {

      let hypertyDescriptorURL = 'hyperty-catalogue://catalogue.sp.domain/.well-known/hyperty/HelloHyperty';
      let loadHyperty = runtime.loadHyperty(hypertyDescriptorURL);

      expect(loadHyperty)
      .to.be.fulfilled
      .and.to.be.instanceof(Promise)
      .and.notify(done);

    });

    it('should be deployed', function(done) {

      let hypertyDescriptorURL = 'hyperty-catalogue://catalogue.sp.domain/.well-known/hyperty/HelloHyperty';
      let loadHyperty = runtime.loadHyperty(hypertyDescriptorURL);
      let hypertyResolved = ['runtimeHypertyURL', 'status'];

      expect(loadHyperty).to.eventually.to.have.all.keys(hypertyResolved)
      .and.to.be.fulfilled
      .and.notify(done);

    });

    it('should load an hyperty based on given true value for the reuse', function(done) {

      let hypertyDescriptorURL = 'hyperty-catalogue://catalogue.sp.domain/.well-known/hyperty/HelloHyperty';
      let loadHyperty = runtime.loadHyperty(hypertyDescriptorURL, true);
      let hypertyResolved = ['runtimeHypertyURL', 'status'];

      expect(loadHyperty).to.be.fulfilled
      .and.eventually.to.have.all.keys(hypertyResolved)
      .and.notify(done);

    });

    it('should load an hyperty based on given reuse URL address', function(done) {

      let hypertyDescriptorURL = 'hyperty-catalogue://catalogue.sp.domain/.well-known/hyperty/HelloHyperty';
      let loadHyperty = runtime.loadHyperty(hypertyDescriptorURL, 'hyperty://sp.domain/1');
      let hypertyResolved = {
        runtimeHypertyURL: 'hyperty://sp.domain/1',
        status: 'deployed'
      };

      expect(loadHyperty).to.eventually.to.deep.equal(hypertyResolved)
      .and.to.be.fulfilled
      .and.notify(done);

    });


  });

  describe('loadStub(sp-domain)', function() {

    it('should throw when given no arguments', function(done) {
      let spDomain = 'sp.domain';
      let loadStubPromise = runtime.loadStub(spDomain);

      expect(loadStubPromise).to.be.fulfilled.and.notify(done);
    });

    it('should be a Promise', function(done) {
      let spDomain = 'sp.domain';
      let loadStubPromise = runtime.loadStub(spDomain);

      expect(loadStubPromise).to.be.fulfilled
      .to.be.instanceof(Promise)
      .and.notify(done);
    });

    it('should be deployed', function(done) {
      let spDomain = 'sp.domain';
      let loadStubPromise = runtime.loadStub(spDomain);

      //let stubResolved = ['url', 'status'];

      expect(loadStubPromise).to.be.fulfilled
      .and.eventually.have.all.keys('url', 'status', 'descriptorURL')
      .and.eventually.to.have.property('url').to.include('runtime://sp.domain/protostub/')
      .and.notify(done);
    });

  });

  describe('loadIdpProxy(google.com)', function() {

    it('should throw when given no arguments', function(done) {
      let domain = 'google.com';
      let loadIdpPromise = runtime.loadIdpProxy(domain);

      expect(loadIdpPromise).to.be.fulfilled.and.notify(done);
    });

    it('should be a Promise', function(done) {
      let domain = 'google.com';
      let loadIdpPromise = runtime.loadIdpProxy(domain);

      expect(loadIdpPromise).to.be.fulfilled
      .to.be.instanceof(Promise)
      .and.notify(done);
    });

    it('should be deployed', function(done) {
      let domain = 'google.com';
      let loadIdpPromise = runtime.loadIdpProxy(domain);
      let stubResolved = ['url', 'status'];

      expect(loadIdpPromise).to.be.fulfilled
      .and.eventually.to.have.all.keys(stubResolved)
      .and.notify(done);
    });

  });

});

import SandboxRegistry from '../src/sandbox/SandboxRegistry';
import Sandbox from '../src/sandbox/Sandbox';

import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';

let expect = chai.expect;
chai.use(chaiAsPromised);

describe('Sandbox', function() {
  let externalURL = 'hyperty-runtime://sandbox/external';
  let internalURL = 'hyperty-runtime://sandbox/internal';

  it('deploy and undeploy', function(done) {
    let deploySend;

    let sb = new Sandbox();
    sb._onPostMessage = (msg) => {
      console.log(msg);
      if (msg.id === 1) {
        expect(msg).to.eql({
          id: 1, type: 'create', from: externalURL, to: internalURL,
          body: { url: 'hyperty://fake-url', sourceCode: '<source code>', config: {init: '<init>'} }
        });
      }

      if (msg.id  === 2) {
        expect(msg).to.eql({
          id: 2, type: 'delete', from: externalURL, to: internalURL,
          body: { url: 'hyperty://fake-url' }
        });
      }

      deploySend(msg);
    };

    let bus = {
      addListener: (url, callback) => {
        expect(url).to.eql(internalURL);
        deploySend = callback;
      },

      postMessage: (msg) => {
        expect(msg).to.eql({
          id: msg.id, type: 'response', from: internalURL, to: externalURL,
          body: { code: 200 }
        });

        sb._onMessage(msg);
      },

      removeAllListenersOf: (url) => {
        expect(url).to.eql('hyperty://fake-url');
      }
    };

    let sbr = new SandboxRegistry(bus);
    sbr._create = (url, sourceCode, config) => {
      expect(url).to.eql('hyperty://fake-url');
      expect(sourceCode).to.eql('<source code>');
      expect(config).to.eql({init: '<init>'});
      return '<instance>';
    };

    expect(sb.deployComponent('<source code>', 'hyperty://fake-url', {init: '<init>'}).then((deployReply) => {
      expect(deployReply).to.eql('deployed');
      expect(sbr.components).to.eql({'hyperty://fake-url': '<instance>'});

      return sb.removeComponent('hyperty://fake-url').then((unDeployReply) => {
        expect(unDeployReply).to.eql('undeployed');
        expect(sbr.components).to.eql({});
      });
    })).notify(done);
  });

  it('deploy url duplication rejected', function(done) {
    let deploySend;

    let sb = new Sandbox();
    sb._onPostMessage = (msg) => { deploySend(msg); };

    let bus = {
      addListener: (url, callback) => {
        deploySend = callback;
      },

      postMessage: (msg) => {
        sb._onMessage(msg);
      }
    };

    let sbr = new SandboxRegistry(bus);
    sbr._create = (url, sourceCode, config) => {
      return '<instance>';
    };

    expect(sb.deployComponent('<source code>', 'hyperty://fake-url', {init: '<init>'}).then((deployReply) => {
      expect(deployReply).to.eql('deployed');
      expect(sbr.components).to.eql({'hyperty://fake-url': '<instance>'});
      return expect(sb.deployComponent('<source code>', 'hyperty://fake-url', {init: '<init>'})).to.be.rejectedWith('Instance hyperty://fake-url already exist!');
    })).notify(done);
  });
});

import { runtimeFactory } from './resources/runtimeFactory';
import {Syncher, DataObjectReporter, DataObjectObserver} from 'service-framework/dist/Syncher';
import SyncherManager from '../src/syncher/SyncherManager';
import DataObjectsStorage from '../src//store-objects/DataObjectsStorage';
import MessageBus from '../src/bus/MessageBus';

import PEP from '../src/policy/PEP';
import RuntimeCoreCtx from '../src/policy/context/RuntimeCoreCtx';

import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';

chai.config.truncateThreshold = 0;

let expect = chai.expect;
chai.use(chaiAsPromised);

describe('SyncherManager', function() {
  let storageManager = runtimeFactory.storageManager();
  let dataObjectsStorage = new DataObjectsStorage(storageManager, {});

  let schemaURL = 'schema://fake-schema-url';
  let runtimeURL = 'hyperty-runtime://fake-runtime';

  let objURL = 'resource://obj1';
  let objURLChanges = objURL + '/changes';

  let hyperURL1 = 'hyperty://h1.domain/h1';
  let hyperURL2 = 'hyperty://h2.domain/h2';

  let initialData = {
    communication: { name: 'chat-x' },
    x: 10, y: 10
  };

  let msgNodeResponseFunc = (bus, msg) => {

    if (msg.type === 'subscribe') {
      if (msg.id === 2) {
        //reporter subscribe
        expect(msg).to.contain.all.keys({
          id: 2, type: 'subscribe', from: 'hyperty-runtime://fake-runtime/sm', to: 'domain://msg-node.h1.domain/sm',
          body: { resources: [objURL + '/children/children1', objURL + '/children/children2'], source: hyperURL1 }
        });
      } else {
        //observer subscribe
        expect(msg).to.contain.all.keys({
          id: 5, type: 'subscribe', from: 'hyperty-runtime://fake-runtime/sm', to: 'domain://msg-node.obj1/sm',
          body: { resources: [objURL + '/changes', objURL + '/children/children1', objURL + '/children/children2'], source: hyperURL2 }
        });
      }

      //simulate msg-node response
      bus.postMessage({
        id: msg.id, type: 'response', from: msg.to, to: msg.from,
        body: { code: 200 }
      });
    }
  };

  //fake object allocator -> always return the same URL
  let allocator = {
    create: () => {
      return new Promise((resolve) => {
        resolve({address: [objURL]});
      });
    }
  };

  let registry = {
    registerDataObject: (objectRegistration) => {
      console.log('REGISTRY-OBJECT: ', objectRegistration);
      return new Promise((resolve) => {
        resolve('ok');
      });
    },

    isInterworkingProtoStub: (url) => {
      console.log('isInterworkingProtoStub: ', url);
      return false;
    },

    getPreAuthSubscribers: () => {
      return ['hyperty://domain/hyperty-instance'];
    },
    getHypertyName: () => {
      return 'HypertyChat';
    },
    isDataObjectURL: (dataObjectURL) => {
      let splitURL = dataObjectURL.split.skip('://');
      return splitURL[0] === 'comm';
    },
    registerSubscribedDataObject: () => {},
    registerSubscriber: () => {},
    isLocal: (url) => {
      console.log('isLocal: ', url);
      return false;
    },
    runtimeURL: 'runtime://localhost/7601'
  };

  let identityModule = {
    decryptMessage: (message) => {
      return new Promise((resolve) => {
        resolve(message);
      });
    },
    encryptMessage: (message) => {
      return new Promise((resolve) => {
        resolve(message);
      });
    },
    getToken: () => {
      return new Promise((resolve) => {
        resolve({ userProfile: {username: 'user@domain' } });
      });
    }
  };

  let catalog = {
    getDataSchemaDescriptor: (schema) => {
      console.log('REQUEST-SCHEMA: ', schema);
      return new Promise((resolve, reject) => {
        if (schema) {
          resolve({ sourcePackage: { sourceCode: {
            properties: {
              scheme: { constant: 'resource' },
              children: { constant: ['children1', 'children2'] }
            }
          }}});
        } else {
          reject('No schema provided');
        }
      });
    }
  };

  let runtimeCoreCtx = new RuntimeCoreCtx(runtimeURL, identityModule, registry, storageManager, runtimeFactory.runtimeCapabilities());
  let policyEngine = new PEP(runtimeCoreCtx);

  let handlers = [

    // Policy message authorise
    function(ctx) {
      policyEngine.authorise(ctx.msg).then(function(changedMgs) {

        changedMgs.body.identity = {
          userProfile: {
            userURL: 'user://user@domain.pt'
          }
        };

        ctx.msg = changedMgs;
        ctx.next();
      }).catch(function(reason) {
        console.error(reason);
        ctx.fail(reason);
      });
    }
  ];

  it('reporter read', function(done) {
    let bus = new MessageBus();

    bus._onPostMessage = (msg) => {
      console.log('_onPostMessage: ', msg);
      msgNodeResponseFunc(bus, msg);
    };

    new SyncherManager(runtimeURL, bus, registry, catalog, storageManager, allocator, dataObjectsStorage, identityModule);

    let sync2 = new Syncher(hyperURL2, bus, { runtimeURL: runtimeURL });
    let sync1 = new Syncher(hyperURL1, bus, { runtimeURL: runtimeURL });
    sync1.create(schemaURL, [], initialData).then((dor) => {
      console.log('on-create-reply', dor.onRead);
      dor.onRead((event) => {
