import {divideURL} from '../utils/utils';
import {CatalogueFactory} from 'service-framework';
import {HypertyDescriptor, ProtocolStubDescriptor, SourcePackage} from 'service-framework';

class RuntimeCatalogue {

    constructor(nodeHttp, nodeHttps) {
        // console.log('runtime catalogue');
        let _this = this;
        _this._nodeHttp = nodeHttp;
        _this._nodeHttps = nodeHttps;
        _this._factory = new CatalogueFactory(false, undefined);
    }

    set runtimeURL(runtimeURL) {
        let _this = this;
        _this._runtimeURL = runtimeURL;
    }

    get runtimeURL() {
        let _this = this;
        return _this._runtimeURL;
    }

    /**
     * Get hypertyRuntimeURL
     */
    getHypertyRuntimeURL() {
        // TODO: check if this is real needed;
        return _hypertyRuntimeURL;
    }

    /**
     * make a http request to a given URL.
     * @param url
     * @returns {Promise}
     * @private
     */
    _makeExternalRequest(url, nodeHttp, nodeHttps) {
        // console.log("_makeExternalRequest", url);

        // TODO: make this request compatible with nodejs
        // at this moment, XMLHttpRequest only is compatible with browser implementation
        // nodejs doesn't support;
        return new Promise(function (resolve, reject) {
            let protocolmap = {
                "hyperty-catalogue://": "https://",
                "https://": "https://",
                "http://": "http://"
            };

            let usedProtocol;

            let foundProtocol = false;
            for (var protocol in protocolmap) {
                if (url.slice(0, protocol.length) == protocol) {
                    // console.log("exchanging " + protocol + " with " + protocolmap[protocol]);
                    url = protocolmap[protocol] + url.slice(protocol.length, url.length);
                    usedProtocol = protocolmap[protocol];
                    foundProtocol = true;
                    break;
                }
            }

            if (!foundProtocol) {
                reject("Invalid protocol of url: " + url);
                return;
            }

            // nodejs specific http implementations for http & https
            let nodeRequest;
            if (nodeHttp && usedProtocol === "http://") {
                nodeRequest = nodeHttp;
            } else if (nodeHttps && usedProtocol === "https://") {
                nodeRequest = nodeHttps;
            }

            if (nodeRequest) {
                // request should be the same for http & https

                // get url without protocol
                let hostAndPath = url.slice(usedProtocol.length, url.length);

                // get host (+ port)
                let host = hostAndPath.slice(0, hostAndPath.indexOf("/"));

                // get path
                let path = hostAndPath.slice(host.length, hostAndPath.length);

                // if host has port, extract port and remove it from host
                let port;
                if (host.indexOf(":") !== -1) {
                    port = host.slice(host.indexOf(":") + 1, host.length);
                    host = host.slice(0, host.indexOf(":"));
                }

                // FIXME: remove rejectUnauthorized when catalogue is using valid certificates
                // FIXME: add error handling
                nodeRequest.get({
                    host: host,
                    port: port,
                    path: path,
                    rejectUnauthorized: false
                }, function (response) {
                    var body = "";
                    response.on("data", function(d) {
                        body += d;
                    });
                    response.on("end", function() {
                        resolve(body);
                    });
                });
            } else if (typeof XMLHttpRequest !== 'undefined') {
                // generic request
                let xhr = new XMLHttpRequest();
                // console.log(url);
                xhr.open('GET', url, true);
                xhr.onreadystatechange = function (event) {
                    let xhr = event.currentTarget;
                    if (xhr.readyState === 4) {
                        // console.log("got response:", xhr);
                        if (xhr.status === 200) {
                            resolve(xhr.responseText);
                        } else {
                            // console.log("rejecting promise because of response code: 200 != ", xhr.status);
                            reject(xhr.responseText);
                        }
                    }
                };


                xhr.send();
            } else {
                reject("no suitable implementation to send request for protocol '" + usedProtocol + "'.");
            }
        });

    }

    /**
     * Get HypertyDescriptor
     * @param hypertyURL - e.g. mydomain.com/.well-known/hyperty/MyHyperty
     * @returns {Promise}
     */
    getHypertyDescriptor(hypertyURL) {
        let _this = this;
        // console.log("getHypertyDescriptor", hypertyURL);

        return new Promise(function (resolve, reject) {

            // request the json
            _this._makeExternalRequest(hypertyURL, _this._nodeHttp, _this._nodeHttps).then(function (result) {
                result = JSON.parse(result);


                if (result["ERROR"]) {
                    // TODO handle error properly
                    reject(result);
                } else {
                    // console.log("creating hyperty descriptor based on: ", result);

                    // create the descriptor
                    let hyperty = _this._factory.createHypertyDescriptorObject(
                        result["cguid"],
                        result["objectName"],
                        result["description"],
                        result["language"],
                        result["sourcePackageURL"],
                        result["type"] || result["hypertyType"],
                        result["dataObjects"]
                    );

                    // console.log("created hyperty descriptor object:", hyperty);

                    // parse and attach sourcePackage
                    let sourcePackage = result["sourcePackage"];
                    if (sourcePackage) {
                        // console.log("hyperty has sourcePackage:", sourcePackage);
                        hyperty.sourcePackage = _this._createSourcePackage(_this._factory, sourcePackage);
                    }

                    resolve(hyperty);
                }
            });
        });
    }

    /**
     * Get RuntimeDescriptor
     * @param runtimeURL - e.g. mydomain.com/.well-known/runtime/MyRuntime
     * @returns {Promise}
     */
    getRuntimeDescriptor(runtimeURL) {
        let _this = this;
        // console.log("getRuntimeDescriptor", runtimeURL);

        return new Promise(function (resolve, reject) {

            // request the json
            _this._makeExternalRequest(runtimeURL, _this._nodeHttp, _this._nodeHttps).then(function (result) {
                result = JSON.parse(result);


                if (result["ERROR"]) {
                    // TODO handle error properly
                    reject(result);
                } else {

                    // parse capabilities first
                    try {
                        result["hypertyCapabilities"] = JSON.parse(result["hypertyCapabilities"]);
                        result["protocolCapabilities"] = JSON.parse(result["protocolCapabilities"]);
                    } catch (e) {
                        // already json object
                    }
                    console.log("creating runtime descriptor based on: ", result);


                    // create the descriptor
                    let runtime = _this._factory.createHypertyRuntimeDescriptorObject(
                        result["cguid"],
                        result["objectName"],
                        result["description"],
                        result["language"],
                        result["sourcePackageURL"],
                        result["type"] || result["runtimeType"],
                        result["hypertyCapabilities"],
                        result["protocolCapabilities"]
                    );

                    console.log("created runtime descriptor object:", runtime);

                    // parse and attach sourcePackage
                    let sourcePackage = result["sourcePackage"];
                    if (sourcePackage) {
                        // console.log("runtime has sourcePackage:", sourcePackage);
                        runtime.sourcePackage = _this._createSourcePackage(_this._factory, sourcePackage);
                    }

                    resolve(runtime);
                }
            });
        });
    }

    /**
     * Get DataSchemaDescriptor
     * @param dataSchemaURL - e.g. mydomain.com/.well-known/dataschema/MyDataSchema
     * @returns {Promise}
     */
    getDataSchemaDescriptor(dataSchemaURL) {
        let _this = this;
        // console.log("getDataSchemaDescriptor", dataSchemaURL);

        return new Promise(function (resolve, reject) {

            // request the json
            _this._makeExternalRequest(dataSchemaURL, _this._nodeHttp, _this._nodeHttps).then(function (result) {
                result = JSON.parse(result);


                if (result["ERROR"]) {
                    // TODO handle error properly
                    reject(result);
                } else {
                    console.log("creating dataSchema based on: ", result);

                    // FIXME: accessControlPolicy field not needed?
                    // create the descriptor
                    let dataSchema = _this._factory.createDataObjectSchema(
                        result["cguid"],
                        result["objectName"],
                        result["description"],
                        result["language"],
                        result["sourcePackageURL"]
                    );

                    console.log("created dataSchema descriptor object:", dataSchema);

                    // parse and attach sourcePackage
                    let sourcePackage = result["sourcePackage"];
                    if (sourcePackage) {
                        // console.log("dataSchema has sourcePackage:", sourcePackage);
                        dataSchema.sourcePackage = _this._createSourcePackage(_this._factory, sourcePackage);
                    }

                    resolve(dataSchema);
                }
            });
        });
    }

    /**
     * Get source Package from a URL
     * @param sourcePackageURL - e.g. mydomain.com/.well-known/hyperty/MyHyperty/sourcePackage
     * @returns {Promise}
     */
    getSourcePackageFromURL(sourcePackageURL) {
        let _this = this;

         //console.log("getting sourcePackage from:", sourcePackageURL);

        return new Promise(function (resolve, reject) {
            _this._makeExternalRequest(sourcePackageURL, _this._nodeHttp, _this._nodeHttps).then(function (result) {
                 //console.log("got raw sourcePackage:", result);
                if (result["ERROR"]) {
                    // TODO handle error properly
                    reject(result);
                } else {
                    result = JSON.parse(result);
                    let sourcePackage = _this._createSourcePackage(_this._factory, result);
                    resolve(sourcePackage);
                }
            }).catch(function (reason) {
                reject(reason);
            });

        });

    }

    /**
     * Get StubDescriptor
     * @param stubURL - e.g. mydomain.com/.well-known/protostub/MyProtostub
     * @returns {Promise}
     */
    getStubDescriptor(stubURL) {
        let _this = this;

        // console.log("getting stub descriptor from: " + stubURL);
        return new Promise(function (resolve, reject) {

          let dividedURL = divideURL(stubURL);
          let domain = dividedURL.domain;
          let protoStub = dividedURL.identity;

          if (!domain) {
            domain = stubURL;
          }

          if (!protoStub) {
            protoStub = 'default';
          } else {
            protoStub = protoStub.substring(protoStub.lastIndexOf('/') + 1);
          }

            let url = 'hyperty-catalogue://' + domain + '/.well-known/protocolstub/' + protoStub;

            _this._makeExternalRequest(url, _this._nodeHttp, _this._nodeHttps).then(function (result) {
                // console.log("makeExternalRequest returned: ", result);

                result = JSON.parse(result);
                // console.log("parsed result: ", result);

                if (result["ERROR"]) {
                    // TODO handle error properly
                    reject(result);
                } else {
                    // console.log("creating stub descriptor based on: ", result);

                    // create the descriptor
                    let stub = _this._factory.createProtoStubDescriptorObject(
                        result["cguid"],
                        result["objectName"],
                        result["description"],
                        result["language"],
                        result["sourcePackageURL"],
                        result["messageSchemas"],
                        result["configuration"],
                        result["constraints"]
                    );

                    // parse and attach the sourcePackage
                    let sourcePackage = result["sourcePackage"];
                    if (sourcePackage) {
                        sourcePackage = _this._createSourcePackage(_this._factory, sourcePackage);
                        stub.sourcePackage = sourcePackage;
                    }
                    resolve(stub);
                }
            });
        });

    }

    /**
     * Returns the sourceCode of a given descriptor
     * @param descriptor - Catalogue Object that was retrieved using e.g. getHypertyDescriptor()
     * @returns {Promise}
     */
    getSourceCodeFromDescriptor(descriptor) {
        let _this = this;
        return new Promise(function (resolve, reject) {
            if (descriptor.sourcePackage) {
                //console.log("descriptor has sourcePackage");
                //console.log("returning sourceCode:", descriptor.sourcePackage.sourceCode);
                resolve(descriptor.sourcePackage.sourceCode);
            } else {
                //console.log("descriptor has no sourcePackage, getting it...");
                let sourcePackage = _this.getSourcePackageFromURL(descriptor.sourcePackageURL).then(function (sourcePackage) {
                    //console.log("got sourcePackage:", sourcePackage);
                    //console.log("returning sourceCode:", sourcePackage.sourceCode);
                    resolve(sourcePackage.sourceCode);
                });
            }
        });
    }

    _createSourcePackage(factory, sp) {
         //console.log("creating sourcePackage. factory:", factory, ", raw package:", sp);
        try {
            sp = JSON.parse(sp);
        } catch (e) {
             console.log("parsing sourcePackage failed. already parsed? -> ", sp);
        }

        // check encoding
        if (sp["encoding"] === "base64") {
            sp["sourceCode"] = atob(sp["sourceCode"]);
        }

        let sourcePackage = factory.createSourcePackage(sp["sourceCodeClassname"], sp["sourceCode"]);
        if (sp["encoding"])
            sourcePackage.encoding = sp["encoding"];

        if (sp["signature"])
            sourcePackage.signature = sp["signature"];

        return sourcePackage;
    }

}

export default RuntimeCatalogue;

//Main dependecies
import Registry from '../registry/Registry';
import IdentityModule from '../identity/IdentityModule';
import PolicyEngine from '../policy/PolicyEngine';
import MessageBus from '../bus/MessageBus';

import RuntimeCatalogue from './RuntimeCatalogue-Local';

import SyncherManager from '../syncher/SyncherManager';

import {divideURL, emptyObject} from '../utils/utils';

/**
 * Runtime User Agent Interface will process all the dependecies of the core runtime;
 * @author Vitor Silva [vitor-t-silva@telecom.pt]
 * @version 0.2.0
 *
 * @property {sandboxFactory} sandboxFactory - Specific implementation of sandbox;
 * @property {RuntimeCatalogue} runtimeCatalogue - Catalogue of components can be installed;
 * @property {runtimeURL} runtimeURL - This identify the core runtime, should be unique;
 * @property {IdentityModule} identityModule - Identity Module;
 * @property {PolicyEngine} policyEngine - Policy Engine Module;
 * @property {Registry} registry - Registry Module;
 * @property {MessageBus} messageBus - Message Bus is used like a router to redirect the messages from one component to other(s)
 */
class RuntimeUA {

  /**
   * Create a new instance of Runtime User Agent
   * @param {sandboxFactory} sandboxFactory - Specific implementation for the environment where the core runtime will run;
   * @param {domain} domainURL - specify the domain base for the runtime;
   */
  constructor(sandboxFactory, domain) {

    if (!sandboxFactory) throw new Error('The sandbox factory is a needed parameter');
    if (!domain) throw new Error('You need the domain of runtime');

    let _this = this;

    _this.sandboxFactory = sandboxFactory;

    _this.runtimeCatalogue = new RuntimeCatalogue();

    // TODO: post and return registry/hypertyRuntimeInstance to and from Back-end Service
    // the response is like: runtime://sp1/123

    let runtimeURL = 'runtime://' + domain + '/' + Math.floor((Math.random() * 10000) + 1);
    _this.runtimeURL = runtimeURL;
    _this.domain = domain;

    // TODO: check if runtime catalogue need the runtimeURL;
    _this.runtimeCatalogue.runtimeURL = runtimeURL;

    // Instantiate the identity Module
    _this.identityModule = new IdentityModule();

    // Use the sandbox factory to create an AppSandbox;
    // In the future can be decided by policyEngine if we need
    // create a AppSandbox or not;
    let appSandbox = sandboxFactory.createAppSandbox();

    // Instantiate the Registry Module
    _this.registry = new Registry(runtimeURL, appSandbox, _this.identityModule);

    // Instantiate the Policy Engine
    _this.policyEngine = new PolicyEngine(_this.identityModule, _this.registry);

    // Instantiate the Message Bus
    _this.messageBus = new MessageBus(_this.registry);
    _this.messageBus.pipeline.handlers = [

      // Policy message authorise
      function(ctx) {
        _this.policyEngine.authorise(ctx.msg).then(function(changedMgs) {
          ctx.msg = changedMgs;
          ctx.next();
        }).catch(function(reason) {
          console.error(reason);
          ctx.fail(reason);
        });
      }
    ];

    // Add to App Sandbox the listener;
    appSandbox.addListener('*', function(msg) {
      _this.messageBus.postMessage(msg);
    });

    // Register messageBus on Registry
    _this.registry.messageBus = _this.messageBus;

    _this.registry.addEventListener('runtime:loadStub', function(domainURL) {

      _this.loadStub(domainURL).then(function() {
        _this.registry.trigger('runtime:stubLoaded', domainURL);
      }).catch(function(reason) {
        console.error(reason);
      });
    });

    // Use sandbox factory to use specific methods
    // and set the message bus to the factory
    sandboxFactory.messageBus = _this.messageBus;

    // Instanciate the SyncherManager;
    _this.syncherManager = new SyncherManager(_this.runtimeURL, _this.messageBus, { }, _this.runtimeCatalogue);

  }

  /**
  * Accomodate interoperability in H2H and proto on the fly for newly discovered devices in M2M
  * @param  {CatalogueDataObject.HypertyDescriptor}   descriptor    descriptor
  */
  discoverHiperty(descriptor) {
    // Body...
  }

  /**
  * Register Hyperty deployed by the App that is passed as input parameter. To be used when App and Hyperties are from the same domain otherwise the RuntimeUA will raise an exception and the App has to use the loadHyperty(..) function.
  * @param  {Object} Object                   hypertyInstance
  * @param  {URL.HypertyCatalogueURL}         descriptor      descriptor
  */
  registerHyperty(hypertyInstance, descriptor) {
    // Body...
  }

  /**
  * Deploy Hyperty from Catalogue URL
  * @param  {URL.HypertyCatalogueURL}    hyperty hypertyDescriptor url;
  */
  loadHyperty(hypertyDescriptorURL) {

    let _this = this;

    if (!hypertyDescriptorURL) throw new Error('Hyperty descriptor url parameter is needed');

    return new Promise(function(resolve, reject) {

      let _hypertyURL;
      let _hypertySandbox;
      let _hypertyDescriptor;
      let _hypertySourcePackage;

      let errorReason = function(reason) {
        reject(reason);
      };

      // Get Hyperty descriptor
      // TODO: the request Module should be changed,
      // because at this moment it is incompatible with nodejs;
      // Probably we need to pass a factory like we do for sandboxes;
      console.info('------------------ Hyperty ------------------------');
      console.info('Get hyperty descriptor for :', hypertyDescriptorURL);
      _this.runtimeCatalogue.getHypertyDescriptor(hypertyDescriptorURL).then(function(hypertyDescriptor) {
        // at this point, we have completed "step 2 and 3" as shown in https://github.com/reTHINK-project/core-framework/blob/master/docs/specs/runtime/dynamic-view/basics/deploy-hyperty.md
        console.info('1: return hyperty descriptor', hypertyDescriptor);

        // hyperty contains the full path of the catalogue URL, e.g.
        // catalogue.rethink.eu/.well-known/..........
        _hypertyDescriptor = hypertyDescriptor;

        let sourcePackageURL = hypertyDescriptor.sourcePackageURL;

        if (sourcePackageURL === '/sourcePackage') {
          return hypertyDescriptor.sourcePackage;
        }

        // Get the hyperty source code
        return _this.runtimeCatalogue.getSourcePackageFromURL(sourcePackageURL);
      })
      .then(function(sourcePackage) {
        console.info('2: return hyperty source code');

        // at this point, we have completed "step 4 and 5" as shown in https://github.com/reTHINK-project/core-framework/blob/master/docs/specs/runtime/dynamic-view/basics/deploy-hyperty.md

        _hypertySourcePackage = sourcePackage;

        //
        // steps 6 -- 9 are skipped.
        // TODO: on release of core 0.2;
        // TODO: Promise to check the policy engine

        // mock-up code;
        // temporary code, only
        let policy = true;

        return policy;
      })
      .then(function(policyResult) {
        console.info('3: return policy engine result: ', policyResult);

        // we have completed step 6 to 9 of https://github.com/reTHINK-project/core-framework/blob/master/docs/specs/runtime/dynamic-view/basics/deploy-hyperty.md right now.
        //
        // Steps 6 -- 9
        // As a result of the sipped steps, we know at this point if we execute
        // inSameSandbox or not.
        //

        // For testing, just assume we execute in same Sandbox.
        let inSameSandbox = true;
        let sandbox;

        if (inSameSandbox) {

          // this don't need be a Promise;
          sandbox = _this.registry.getAppSandbox();

          // we have completed step 11 here.
        } else {

          let domain = divideURL(hypertyDescriptorURL).domain;

          // getSandbox, this will return a promise;
          sandbox = _this.registry.getSandbox(domain);
        }

        // this will return the sandbox or one promise to getSandbox;
        return sandbox;
      }).then(function(sandbox) {
        console.info('4: return the sandbox', sandbox);

        // Return the sandbox indepentely if it running in the same sandbox or not
        // we have completed step 14 here.
        return sandbox;
      }, function(reason) {
        console.error('4.1: try to register a new sandbox', reason);

        // check if the sandbox is registed for this hyperty descriptor url;
        // Make Steps xxx --- xxx
        // Instantiate the Sandbox
        let sandbox = _this.sandboxFactory.createSandbox();

        sandbox.addListener('*', function(msg) {
          _this.messageBus.postMessage(msg);
        });

        return sandbox;
      })
      .then(function(sandbox) {
        console.info('5: return sandbox and register');

        _hypertySandbox = sandbox;

        // Register hyperty
        return _this.registry.registerHyperty(sandbox, hypertyDescriptorURL);
      })
      .then(function(hypertyURL) {
        console.info('6: Hyperty url, after register hyperty', hypertyURL);

        // we have completed step 16 of https://github.com/reTHINK-project/core-framework/blob/master/docs/specs/runtime/dynamic-view/basics/deploy-hyperty.md right now.

        _hypertyURL = hypertyURL;

        console.log(_hypertyDescriptor);

        // Extend original hyperty configuration;
        let configuration = {};
        if (!emptyObject(_hypertyDescriptor.configuration)) {
          configuration = Object.assign({}, JSON.parse(_hypertyDescriptor.configuration));
        }
        configuration.runtimeURL = _this.runtimeURL;

        // We will deploy the component - step 17 of https://github.com/reTHINK-project/core-framework/blob/master/docs/specs/runtime/dynamic-view/basics/deploy-hyperty.md right now.
        return _hypertySandbox.deployComponent(_hypertySourcePackage.sourceCode, _hypertyURL, configuration);
      })
      .then(function(deployComponentStatus) {
        console.info('7: Deploy component status for hyperty: ', deployComponentStatus);

        // we have completed step 19 https://github.com/reTHINK-project/core-framework/blob/master/docs/specs/runtime/dynamic-view/basics/deploy-hyperty.md right now.

        // Add the message bus listener to the appSandbox or hypertSandbox;
        _this.messageBus.addListener(_hypertyURL, function(msg) {
          _hypertySandbox.postMessage(msg);
        });

        // we have completed step 20 of https://github.com/reTHINK-project/core-framework/blob/master/docs/specs/runtime/dynamic-view/basics/deploy-hyperty.md right now.
        let hyperty = {
          runtimeHypertyURL: _hypertyURL,
          status: deployComponentStatus
        };

        resolve(hyperty);

        // we have completed step 21 https://github.com/reTHINK-project/core-framework/blob/master/docs/specs/runtime/dynamic-view/basics/deploy-hyperty.md right now.
        console.log('------------------ END ------------------------');
      })
      .catch(errorReason);

    });

  }

  /**
  * Deploy Stub from Catalogue URL or domain url
  * @param  {URL.URL}     domain          domain
  */
  loadStub(protostubURL) {

    let _this = this;

    if (!protostubURL) throw new Error('domain parameter is needed');

    return new Promise(function(resolve, reject) {

      let domain = divideURL(protostubURL).domain;

      if (!domain) {
        domain = protostubURL;
      }

      let _stubSandbox;
      let _stubDescriptor;
      let _runtimeProtoStubURL;
      let _stubSourcePackage;

      let errorReason = function(reason) {
        console.error(reason);
        reject(reason);
      };

      // Discover Protocol Stub
      console.info('------------------- ProtoStub ---------------------------\n');
      console.info('Discover or Create a new ProtoStub for domain: ', domain);
      _this.registry.discoverProtostub(domain).then(function(descriptor) {
        // Is registed?
        console.info('1. Proto Stub Discovered: ', descriptor);
        _stubDescriptor = descriptor;

        // we have completed step 2 https://github.com/reTHINK-project/core-framework/blob/master/docs/specs/runtime/dynamic-view/basics/deploy-protostub.md

