        //   expect(changeEvent).to.contain.all.keys({ cType: 'add', oType: 'object', field: 'test', data: ['a', 'b', 'c'] });
        // });

        done();
      });

      a.data.test = ['a', 'b', 'c'];

    }).catch((error) => {
