        return s4() + s4() + '-' + s4() + '-' + s4() + '-' +
          s4() + '-' + s4() + s4() + s4();
      }

      let objURL = 'resource://domain/' + guid();

      //fake object allocator -> always return the same URL
      let allocator = {
        create: () => {
          return new Promise((resolve) => {
            hyperties.object = objURL;
            resolve({address: [objURL]});
          });
        }
      };

      new SyncherManager(runtimeURL, bus, registry, catalog, storageManager, allocator, dataObjectsStorage, identityModule);

      let hypertyURL3 = 'hyperty://h1.domain/' + guid();
      hyperties.h3 = hypertyURL3;
      let sync3 = new Syncher(hypertyURL3, bus, { runtimeURL: runtimeURL });
      sync3.onNotification((notifyEvent) => {
        sync3.subscribe(schemaURL, notifyEvent.url).then((doo) => {
          //sync3DataObjectObserver = doo;
          console.log('sync3 resources: ', doo.url);
          done();
        });
      });

      let hypertyURL2 = 'hyperty://h1.domain/' + guid();
      hyperties.h2 = hypertyURL2;
      let sync2 = new Syncher(hypertyURL2, bus, { runtimeURL: runtimeURL });
      sync2.onNotification((notifyEvent) => {
        sync2.subscribe(schemaURL, notifyEvent.url).then((doo) => {
          //sync2DataObjectObserver = doo;
          console.log('sync2 resources:', doo.url);
        });
      });

      let hypertyURL1 = 'hyperty://h1.domain/' + guid();
      hyperties.h1 = hypertyURL1;
      let sync1 = new Syncher(hypertyURL1, bus, { runtimeURL: runtimeURL });
      sync1.create(schemaURL, [hypertyURL2, hypertyURL3], initialData, true, false).then((dor) => {
        // sync1DataObjectReporter = dor;
        dor.onSubscription((subscribeEvent) => {
          subscribeEvent.accept();
        });
      });
    });

    it('should resume the url stored on storageManager', (done) => {

      let bus = new MessageBus();
      bus.pipeline.handlers = handlers;
      bus._onPostMessage = (msg) => {
        console.log('10-_onPostMessage: ', msg);

        if (msg.type === 'subscribe') {
          bus.postMessage({
            id: msg.id, type: 'response', from: msg.to, to: msg.from,
            body: { code: 200 }
          });
        }

        // TODO: remove the msg.body.version verification
        // TODO: this could be related with the syncher synchronization mechanism
        if (msg.type === 'update' && msg.body.version === 2) {
          expect(msg.from).to.eql(hyperties.object);
          expect(msg.to).to.eql(hyperties.object + '/changes');

          done();
        }

      };

      //fake object allocator -> always return the same URL
      let allocator = {
        create: function() {
          return new Promise(function(resolve) {
            resolve({address: [hyperties.object]});
          });
        }
      };

      new SyncherManager(runtimeURL, bus, registry, catalog, storageManager, allocator, dataObjectsStorage, identityModule);

      let sync3 = new Syncher(hyperties.h3, bus, { runtimeURL: runtimeURL });
      sync3.onNotification((notifyEvent) => {
        sync3.subscribe(schemaURL, notifyEvent.url).then((doo) => {
          doo.onChange('*', function(changes) {
            console.log('Sync 3: ', changes);
          });
          console.log('sync3 resources: ', doo.url);
        });
      });

      let sync2 = new Syncher(hyperties.h2, bus, { runtimeURL: runtimeURL });
      sync2.onNotification((notifyEvent) => {
        sync2.subscribe(schemaURL, notifyEvent.url).then((doo) => {
          doo.onChange('*', function(changes) {
            console.log('Sync 2: ', changes);
          });
          console.log('sync2 resources:', doo.url);
        });
      });

      let sync1 = new Syncher(hyperties.h1, bus, { runtimeURL: runtimeURL });
      sync1.create(schemaURL, [hyperties.h2, hyperties.h3], initialData).then((dor) => {

        dor.onSubscription((subscribeEvent) => {
          subscribeEvent.accept();

          dor.data.x = 20;
        });
      });

    });

  });

});

// Unit test modules
import chai from 'chai';
import chaiAsPromised from 'chai-as-promised';
import sinonChai from 'sinon-chai';

// internal modules to be tested
import {divideURL, splitObjectURL} from '../src/utils/utils';

let expect = chai.expect;

chai.config.showDiff = true;
chai.config.truncateThreshold = 0;

chai.use(chaiAsPromised);
chai.use(sinonChai);

describe('Utils Module', function() {

  it('should divide an url in 3 parts (type, domain, identity)', function() {

    // http://localhost:9090/test
    // hyperty://localhost:9090/test
    // http://sp1.sp/test-testes
    // hyperty-catalogue://sp1.sp/HelloHyperty
    // runtime://sp1.sp/8456/sm
    // domain://msg-node.sp1.sp/hyperty-address-allocation
    // hyperty-runtime://sp1.sp

    let url = 'runtime://sp.sp/8456/sm';
    expect(divideURL(url)).to.deep.equal({type: 'runtime', domain: 'sp.sp', identity: '/8456/sm'});

    url = 'domain://msg-node.sp1.sp/hyperty-address-allocation';
    expect(divideURL(url)).to.deep.equal({type: 'domain', domain: 'msg-node.sp1.sp', identity: '/hyperty-address-allocation'});

    url = 'hyperty://sp1.sp/HelloHyperty';
    expect(divideURL(url)).to.deep.equal({type: 'hyperty', domain: 'sp1.sp', identity: '/HelloHyperty'});

    url = 'hyperty://localhost:9090/test';
    expect(divideURL(url)).to.deep.equal({type: 'hyperty', domain: 'localhost:9090', identity: '/test'});

    url = 'runtime://sp1.sp';
    expect(divideURL(url)).to.deep.equal({type: 'runtime', domain: 'sp1.sp', identity: ''});

    url = 'http://localhost:9090/test';
    expect(divideURL(url)).to.deep.equal({type: 'http', domain: 'localhost:9090', identity: '/test'});

    url = 'https://localhost:9090/secure-test';
    expect(divideURL(url)).to.deep.equal({type: 'https', domain: 'localhost:9090', identity: '/secure-test'});
  });

  it('should return the dataObjectURL identifier', function() {

    expect(splitObjectURL('comm://localhost/f3b43208-63fa-468f-8fe0-ac3d20ca9dc0/children/chatmessages')).to.deep.eql({
      url: 'comm://localhost/f3b43208-63fa-468f-8fe0-ac3d20ca9dc0',
      resource: 'chatmessages'
    });

    expect(splitObjectURL('comm://localhost/f3b43208-63fa-468f-8fe0-ac3d20ca9dc0/children')).to.deep.eql({
      url: 'comm://localhost/f3b43208-63fa-468f-8fe0-ac3d20ca9dc0',
      resource: undefined
    });

    expect(splitObjectURL('comm://localhost/f3b43208-63fa-468f-8fe0-ac3d20ca9dc0')).to.deep.eql({
      url: 'comm://localhost/f3b43208-63fa-468f-8fe0-ac3d20ca9dc0',
      resource: undefined
    });
  });

});
